def acceptInputs(): 
  while(1):
      if(user.device == 0):
          print("No devices currently playing. Please restart")
          break
      print("Please type 'Pause', 'Play', 'Previous', or 'Next'")
      alteration = input().lower()
      if(alteration in possibleAlterations):
        print(user.alterPlayback(alteration))
      else:
          print('Try again.')

def render_text_on_matrix(ledmatrix, text, start_row, start_col, max_chars, letters_temp):

    char_count = 0
    for char in text:
        if char not in letters_temp:
            continue
        if char_count >= max_chars:
            break
        for row in range(5):
            for col in range(4):
                ledmatrix[start_row + row][start_col + char_count * 4 + col] = (
                    letters_temp[char][row][col] if col != 3 else ["0", "0", "0"]
                )
        char_count += 1

def render_bubbles(ledmatrix, filled_bubbles, start_row, start_col, max_cols):
    bubble_count = 0
    for col in range(max_cols):
        for row in range(3):
            ledmatrix[start_row + row][start_col + col] = (
                ["255", "255", "255"] if bubble_count < filled_bubbles else ["0", "0", "0"]
            )
            bubble_count += 1

def print_led_matrix(ledmatrix):
    for row in ledmatrix:
        print("".join(f"\x1b[48;2;{cell[0]};{cell[1]};{cell[2]}m   \x1b[0m" for cell in row))

def printPlayback():
    previousPlayback = {}
    loopCount = 0
    max_chars = 9

    while True:
        if user.device == 0:
          print("No devices currently playing. Please restart")
          break

        # check if song has changed
        if (not previousPlayback or previousPlayback["item"]["name"] != user.playbackState["item"]["name"]):
          currentSong = user.playbackState["item"]["name"].lower() + "   "
          currentArtist = user.playbackState["item"]["artists"][0]["name"].lower() + "   "
          previousPlayback = user.playbackState
          albumCover = user.playbackState["item"]["album"]["images"][0]["url"]
            

        # calculate progress bubbles number
        song_duration = user.playbackState["item"]["duration_ms"]
        progress = user.playbackState["progress_ms"]
        bubbles_filled = int(progress / song_duration * 99)

        urllib.request.urlretrieve(albumCover, "albumCover.png")
        image = Image.open("albumCover.png")
        image = image.resize((24, 24))
        image.save("resizedAlbumCover.png")
        if(image.mode != "RGB"):
          image = image.convert("RGB")
        imageData = image.getdata()

        # clear the console
        os.system("cls")
        allpixels = list(image.getdata())
        for num in range(24):
            ledmatrix[num + 4][2:26] = allpixels[(num * 24):(24 + num*24)]
        # render song title and artist
        render_text_on_matrix(ledmatrix, currentSong, 6, 27, max_chars, letters_temp)
        render_text_on_matrix(ledmatrix, currentArtist, 13, 27, max_chars, letters_temp)

        # scroll text if too long
        if len(currentSong) > 12:
          currentSong = currentSong[1:] + currentSong[0]
        if len(currentArtist) > 12:
          currentArtist = currentArtist[1:] + currentArtist[0]

        # render bubbles
        render_bubbles(ledmatrix, bubbles_filled, 22, 28, 33)

        # print the matrix
        print_led_matrix(ledmatrix)

        # update playback state every three seconds
        time.sleep(1)
        if loopCount > 2:
          user.playbackState = user.updatePlayback()
          loopCount = 0
        else:
          loopCount += 1
      

printPlayback()
  
